import React, { useState } from 'react'

export default function TextForm(props) {
    const [text, setText] = useState('');
    let [count, setCount] = useState(0);
    let [count1, setCount1] = useState(0);
    let countChar = 0,
    countCons = 0;

 // Function to count Vowels:

 const handleVoClick = () => {
     for (count = 0; count <= text.length; count++) {
       if (text.charAt(count).match(/[aeiouAEIOU]/)) {
         countChar++;
         setCount (countChar);
       }
     }
     // console.log("No. of Vowels are: " + countChar);
   };

    const handleUpClick = () => {
        let newText = text.toUpperCase();
        setText(newText)
    }

    const handleLowClick = () => {
        let newText = text.toLowerCase();
        setText(newText)
    }
    const handleClearClick = () => {
        let newText = '';
        setText(newText)
    }
    const handleOnChange = (event) => {
        setText(event.target.value)
    }

    return (
        <>
            <div className="container">
                <h1>{props.heading}</h1>
                <div className="mb-3">
                <button className="btn btn-danger my-2 float-end" onClick={handleClearClick}>Clear Text</button>

                    <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert To UPPERCASE</button>
                <button className="btn btn-primary mx-2" onClick={handleLowClick}>Convert To lowercase</button>
                <button className="btn btn-primary mx-2" onClick={handleVoClick}>Count Voels</button>
            </div>
            <div className="container my-2">
                <h2>Your Text summary</h2>
                <p>{text.split(" ").length} Words , and {text.length} characters</p>
                <p>{0.008 * text.split(" ").length} minutes read</p>
            </div>
        </>
    )
}
